$(document).ready(function(){$("#todo-list").sortable(),$(".chk-todo input").iCheck({checkboxClass:"icheckbox_minimal-green",radioClass:"iradio_minimal-green"}),$(".chk-todo input").on("ifChecked",function(o){$(this).parents("li").addClass("todo-done")}),$(".chk-todo input").on("ifUnchecked",function(o){$(this).parents("li").removeClass("todo-done")}),$(".todo-remove").on("click",function(o){o.preventDefault(),$(this).parents(".action-todo").parents("li").remove()})});